import React from 'react'

export const MatrixBackground: React.FC = () => {
  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      {/* Primary grid */}
      <div 
        className="absolute inset-0 matrix-grid opacity-25"
        style={{
          transform: 'perspective(1000px) rotateX(60deg) scale(2)',
          transformOrigin: 'center center',
        }}
      />

      {/* Secondary grid for depth */}
      <div 
        className="absolute inset-0 matrix-grid opacity-15"
        style={{
          transform: 'perspective(1000px) rotateX(60deg) scale(1.5) translateY(-20px)',
          transformOrigin: 'center center',
          animationDelay: '2s',
        }}
      />

      {/* Animated particles effect */}
      <div className="absolute inset-0">
        <div className="absolute top-1/4 left-1/4 w-2 h-2 bg-blue-400 rounded-full opacity-60 animate-pulse"></div>
        <div className="absolute top-1/3 right-1/3 w-1 h-1 bg-purple-400 rounded-full opacity-40 animate-pulse" style={{ animationDelay: '1s' }}></div>
        <div className="absolute bottom-1/3 left-1/3 w-1.5 h-1.5 bg-cyan-400 rounded-full opacity-50 animate-pulse" style={{ animationDelay: '2s' }}></div>
        <div className="absolute top-2/3 right-1/4 w-1 h-1 bg-green-400 rounded-full opacity-30 animate-pulse" style={{ animationDelay: '3s' }}></div>
      </div>

      {/* Gradient overlay for depth */}
      <div className="absolute inset-0 bg-gradient-to-b from-transparent via-background/10 to-background/30" />

      {/* Radial gradient for focus */}
      <div className="absolute inset-0 bg-radial-gradient opacity-20" />
    </div>
  )
}
