import React, { useState, useEffect } from 'react'
import { MatrixBackground } from '../components/MatrixBackground'
import { AuthForm } from '../components/AuthForm'

interface LoginPageProps {
  onAuthSuccess: () => void
}

export const LoginPage: React.FC<LoginPageProps> = ({ onAuthSuccess }) => {
  const [currentText, setCurrentText] = useState(0)
  const texts = ['Общайтесь.', 'Автоматизируйте.', 'Создавайте.', 'Упрощайте.']

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentText((prev) => (prev + 1) % texts.length)
    }, 2000)
    return () => clearInterval(interval)
  }, [texts.length])

  return (
    <div className="min-h-screen relative overflow-hidden">
      <MatrixBackground />

      {/* Hero Section */}
      <div className="relative z-10 min-h-screen flex items-center justify-center px-4">
        <div className="w-full max-w-6xl">
          <div className="grid lg:grid-cols-2 gap-16 items-center">
            {/* Левая колонка - Hero Content */}
            <div className="text-center lg:text-left space-y-8">
              {/* Главный заголовок */}
              <div className="space-y-6">
                {/* Название продукта */}
                <div>
                  <h1 className="text-6xl lg:text-8xl font-bold font-pixel bg-gradient-to-r from-white via-blue-200 to-purple-200 bg-clip-text text-transparent">
                    less
                  </h1>
                </div>

                {/* Описание что делает */}
                <div>
                  <h2 className="text-2xl lg:text-3xl font-pixel text-gray-400 font-normal">
                    workflow guru
                  </h2>
                </div>

                {/* Анимированный текст */}
                <div className="h-12 flex justify-center lg:justify-start items-center">
                  <span className="text-xl lg:text-2xl font-pixel text-blue-300 animate-fade-in-out">
                    {texts[currentText]}
                  </span>
                </div>
              </div>

              {/* Подзаголовок */}
              <p className="text-lg lg:text-xl text-gray-300 leading-relaxed max-w-2xl font-pixel">
                AI, у которого за спиной сотни готовых автоматизаций. Он не учится на тебе. 
                Он уже натренирован на реальных решениях интегрированных в бизнесы.
              </p>
            </div>

            {/* Правая колонка - Форма авторизации */}
            <div className="flex justify-center">
              <div className="w-full max-w-md">
                <AuthForm onAuthSuccess={onAuthSuccess} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
